import TaskItem from "./TaskItem";
import {useState} from "react";

export default function TaskList(props){

    const [editedID, setEditedID] = useState(null);

    return (
        <div>


                    {props.data.map(t =>
                            <TaskItem taskItem={t}
                                      key={t.taskId}
                                      isCompleted={t.isCompleted}
                                      taskName={t.taskName}
                                      handleChange = {props.handleChange}
                                      editedID = {editedID}/>)}


        </div>)
}
