import logo from './logo.svg';
import './App.css';
import './TaskItem';
import './DataContainer';
import {useState} from "react";
import AppHeader from "./AppHeader";
import TaskList from "./TaskList";

function App(props) {
    const [hideUncompleted, setHideUncompleted] = useState(true);
    const [data, setData] = useState(props.data);

    function handleUncompleted(){
        setHideUncompleted(!hideUncompleted);
        props.data.filter(taskItem => !taskItem.isCompleted);
    }
    function handleDelete(){
        setData(data.filter(taskItem => !taskItem.isCompleted))
    }

  return (
      <div>
          <AppHeader/>
          <TaskList data={props.data} handleChange={props.handleChange}/>
          <button className="plus-button" type="button" id="plus" onClick = {props.handlePlusClick}> + </button>
          <label htmlFor="plus" className="newItem"> Create new item </label>
          <br/>
          <button className="uncompleted" type="button" id="showUncom" onClick = {handleUncompleted}
                  data={hideUncompleted? props.data:props.data.filter(taskItem => !taskItem.isCompleted)}>
                  {hideUncompleted? "Hide completed":"Show all items"} </button>
          <button className="deleteCompleted" type="button" id="delete" onClick = {props.handleDelete}> Delete completed items</button>
      </div>

  );
}

export default App;
