import {useState} from "react";
import App from "./App";

export default function DataContainer(props) {
    // state manipulates data
    const [data, setData] = useState(props.data);

    function onHandleChange(taskID, field, value) {
        setData(data.map(taskItem => taskItem.taskId == taskID ? {...taskItem, [field]:value}:taskItem))
    }

    function changeName(id, newName) {
        setData(data.map(task => task.taskId === id ? {...task, taskName: newName}:task));
    }

    return (
        <div>
            <App data={data} changeName={changeName}></App>
            {data.map(task => task.taskName)}
        </div>
    )
}
