function App(props) {
  // state manipulates data
  const [data, setData] = useState(props.data);

  function onHandleChange(taskID, field, value) {
      setData(data.map(taskItem => taskItem.taskId == taskID ? {...taskItem, [field]:value}:taskItem))
  }

  return (
      <div>
      <AppHeader/>
      <TaskList data={props.data}/>
      <input type = "checkbox" id = "checkbox" onChange={(e)=> console.log(e.target.value)}> </input>
      </div>

  );
}

