import './DataContainer'
import {useState} from "react";


export default function TaskItem(props) {
    const task = props.taskItem;

    // console.log(task.taskId, props.editedID)
    return <div>
        <input
            type = "checkbox"
            checked = {props.isCompleted}
            id = {props.taskId}
            onChange={(e) =>
                props.handleChange(task.taskId, "isCompleted", e.target.checked)}
        />

        <input type = "text" value = {props.taskName} id = {props.taskId}
               //onChange={(e)=> }
               onKeyPress={(e) => e.key === 'Enter' && props.handleChange(task.taskId, "taskName", e.target.value)} />

       {/*{task.taskId === props.editedID?:*/}
       {/*     <input type = "text"/>*/}
        {/*     : <div/>}*/}
    </div>
}
